
/*
 * ####################################################################
 * 
 * Problem Description:
 * --------------------
        You are given a string S, and you have to find all the amazing substrings of S.
        An amazing Substring is one that starts with a vowel (a, e, i, o, u, A, E, I, O, U).

        Input
        Only argument given is string S.

        Output
        Return a single integer X mod 10003, here X is the number of Amazing Substrings in given the string.


    Problem Constraints
    -------------------
        1 <= length(S) <= 1e6
        S can have special characters

    Example:
    --------
        Input
            ABEC

        Output
            6

        Explanation
            Amazing substrings of given string are :
            1. A
            2. AB
            3. ABE
            4. ABEC
            5. E
            6. EC
            here number of substrings are 6 and 6 % 10003 = 6.
 * 
 * ####################################################################
 */

using System;
using System.Collections.Generic;
using System.Text;

namespace _027_AmazingSubArrays
{
    class Program
    {
        static void Main(string[] args)
        {
            //string str = "ABEC"; // Ans = 6
            //string str = "ABEDFGIOWNHI"; // Ans = 34
            string str = "...";
            Console.WriteLine(CountAmazingSubStringsV1(str));
        }

        static int CountAmazingSubStrings(string s)
        {
            int count = 0;
            var charElements = s.ToCharArray();

            // below list is needed if we need to return a list of Amazing substrings
            //var prefixConcat = new List<string>() { charElements[0].ToString() };
            var vowelPos = new List<int>();
            FindAndAddVowelPosition(charElements, vowelPos, 0);

            for (int i = 1; i < charElements.Length; i++)
            {
                //prefixConcat.Add(prefixConcat[i - 1] + charElements[i]);
                FindAndAddVowelPosition(charElements, vowelPos, i);
            }

            for (int i = 0; i < vowelPos.Count; i++)
            {
                count += (charElements.Length - vowelPos[i]);
            }

            return count % 10003;

            static void FindAndAddVowelPosition(char[] charElements, List<int> vowelPos, int i)
            {
                string vowelsString = "aeiouAEIOU";
                if (vowelsString.IndexOf(charElements[i]) >= 0)
                {
                    vowelPos.Add(i);
                }
            }
        }

        static int CountAmazingSubStringsV1(string s)
        {
            int count = 0;
            var charElements = s.ToCharArray();

            for (int i = 0; i < charElements.Length; i++)
            {
                count += FindVowelPosition(charElements, i) ? (charElements.Length - i) : 0;
                count %= 10003;
            }

            return count;

            static bool FindVowelPosition(char[] charElements, int i)
            {
                string vowelsString = "aeiouAEIOU";
                return vowelsString.IndexOf(charElements[i]) >= 0;
            }
        }

    }
}
